let pay = msg.payload;

// Read scrolling table from context
let sctab = flow.get("sctab");

// Modify scrolling table by adding msg.payload as first element
let size = sctab.unshift(pay);

// Remove last element from scrolling table
if (size >9) sctab.pop();

// Update context for scrolling text
flow.set("sctab",sctab);

// Shalow copy updated scrolling table to msg.payload
msg.payload = [...sctab];

return msg;

